mapper.select:
SELECT *
FROM files
WHERE p_table = #{pTable}
  AND p_no = #{pNo}
ORDER BY created_at DESC

mapper.insert:
INSERT INTO files(
    id, p_table, p_no, file_name, origin_name, file_path, file_size, type, seq
)
VALUES (
    #{id}, #{pTable}, #{pNo}, #{fileName}, #{originName}, #{filePath}, #{fileSize}, #{type}, #{seq}
)

mapper.update:
UPDATE files
type = #{type}, seq = #{seq}, p_table = #{pTable}, p_no = #{pNo}, file_name = #{fileName}, origin_name = #{originName}, file_path = #{filePath}, file_size = #{fileSize}, updated_at = now()
WHERE no = #{no}

UPDATE files
type = #{type}, seq = #{seq}, p_table = #{pTable}, p_no = #{pNo}, file_name = #{fileName}, origin_name = #{originName}, file_path = #{filePath}, file_size = #{fileSize}, updated_at = now()
WHERE id = #{id}

mapper.delete:
DELETE FROM files
WHERE no IN

    #{no}

DELETE FROM files
WHERE id IN

    #{id}

DELETE FROM files
WHERE p_table = #{pTable}
  AND p_no = #{pNo}

mapper.select:
SELECT *
FROM files
WHERE p_table = #{pTable}
  AND p_no = #{pNo}
  AND type = #{type}
LIMIT 1

SELECT *
FROM files
WHERE p_table = #{pTable}
  AND p_no = #{pNo}
  AND type = #{type}
